import React, { useEffect, useState } from 'react'
import './AddMarks.css';


function AddMarks() {


    let [total, setTotal] = useState(0)
    let [percentage, setPercentage] = useState(0)

    let math = 0;
    let chem = 0;
    let phy = 0;
    let rollNum = null;
    let name = null;


    const mathScore = (event) => {
        math = parseFloat((event.target.value))
    }

    const phyScore = (event) => {
        phy = parseFloat((event.target.value))

    }

    const chemScore = (event) => {
        chem = parseFloat((event.target.value))

    }

    const rollNumber = (event) => {
        rollNum = (event.target.value)
    }
    const nameInput = (event) => {
        name = (event.target.value);
    }

    const getTotal = () => {
        const totalValue = chem + phy + math
        localStorage.setItem("rollNum", rollNum)
        localStorage.setItem("name", name)
        localStorage.setItem("math", math )
        localStorage.setItem("phy", phy )
        localStorage.setItem("chem", chem )
        localStorage.setItem("total", totalValue )
        const total_local = localStorage.getItem("total")
        console.log(total_local)
        setTotal(total_local)
        document.getElementById("total").disabled = true;
        console.log(total)
    }


    const getPercentage = () => {
        const percentageValue = (total/300)*100
        localStorage.setItem("percentage", percentageValue )
        const percentage_local = localStorage.getItem("percentage")
        setPercentage(percentage_local)
        document.getElementById("percentage").disabled = true;
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("submitted")
        console.log({
            "maths": parseFloat(localStorage.getItem("math")),
            "phy": parseFloat(localStorage.getItem("phy")),
            "chem": parseFloat(localStorage.getItem("chem")),
            "total": parseFloat(localStorage.getItem("total")),
            "percentage": parseFloat(localStorage.getItem("percentage")),
            "rollNum": localStorage.getItem("rollNum"),
            "name": localStorage.getItem("name")
        })
        name = null;

        localStorage.setItem("rollNum", null)
        localStorage.setItem("name", null)
        localStorage.setItem("math", null )
        localStorage.setItem("phy", null )
        localStorage.setItem("chem", null )
        localStorage.setItem("total", null )
    }



    return (
        <div className="add-marks">
            <div className="container">
                <h1 className="text-center">Enter Marks</h1>
                <form onSubmit={handleSubmit}>
                    <div className="row">
                        <div className="col-lg-2 col-md-2 col-sm-12"></div>
                        <div className="col-lg-4 col-md-4 col-sm-12">
                            <div class="form-group">
                                <label for="exampleInputEmail1">Enter Name</label>
                                <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={nameInput} placeholder="Enter Name" />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                            <div class="form-group">
                                <label for="exampleInputEmail1">Enter Maths Score</label>
                                <input type="number" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={mathScore} placeholder="Math's Score" />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                            <div class="form-group">
                                <label for="exampleInputEmail1">Enter Physics Score</label>
                                <input type="number" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={phyScore} placeholder="Physics Score" />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                            <div class="form-group">
                                <label for="exampleInputEmail1">Percentage</label>
                                <input type="number" onClick={getPercentage} class="form-control" id="percentage" value={percentage} aria-describedby="emailHelp" />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                        </div>
                        <div className="col-lg-4 col-md-4 col-sm-12">
                            <div class="form-group">
                                <label for="exampleInputEmail1">Enter Roll Number</label>
                                <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={rollNumber} placeholder="Enter Roll Number" />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                            <div class="form-group">
                                <label for="exampleInputEmail1">Enter Chemstry Score</label>
                                <input type="number" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" onChange={chemScore} placeholder="Chemestry Score" />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                            <div class="form-group">
                                <label for="exampleInputEmail1">Total Score</label>
                                <input onClick={getTotal} type="number" class="form-control" id="total" aria-describedby="emailHelp" value={total} />
                                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                            </div>
                            <div class="form-group">
                                <button type="submit" class="btn btn-primary">Submit</button>
                            </div>


                        </div>
                        <div className="col-lg-2 col-md-2 col-sm-12"></div>
                    </div>
                </form>
            </div>
        </div>
    )
}


export default AddMarks
